import React, { useEffect, useState } from "react";
import axios from "axios";
import "../src/css/lan.css";
function Lan() {
  const [name, setname] = useState("");
  const [dep, setdep] = useState("");
  const [col, setcol] = useState("");
  const [io, setio] = useState(false);
  useEffect(() => {
    const od = async () => {
      await axios
        .post("http://localhost:800/off/od", {
          username: sessionStorage.getItem("un"),
        })
        .then((res) => {
          setname(res.data.name);
          setdep(res.data.dep);
          setcol(res.data.col);
          if (res.data.status) {
            setio(true);
          } else {
            setio(false);
          }
        });
    };
    od();
  });
  const i = async () => {
    await axios.post("http://localhost:800/off/in", {
      username: sessionStorage.getItem("un"),
    }).then((res)=>{
      
    })
  };
  const o = () => {
    setio(false);
  };
  return (
    <div className="lanm">
      <div className="lanb">
        <div className="dcil">
          NAME : <span>{name}</span>
        </div>
        <div className="dcil">
          DEPARTMENT : <span>{dep}</span>
        </div>
        <div className="dcil">
          COLLEGE : <span>{col}</span>
        </div>
        <div className="dcil">
          STATUS :{" "}
          {io ? (
            <button onClick={o}>OUT</button>
          ) : (
            <button onClick={i}>IN</button>
          )}
        </div>
      </div>
    </div>
  );
}
export default Lan;
