import React, { useEffect, useState } from "react";
import axios from "axios";
import "../src/css/lan.css";
function Lan() {
  const [name, setname] = useState("");
  const [dep, setdep] = useState("");
  const [col, setcol] = useState("");
  const [si, setsi] = useState("black");
  const [so, setso] = useState("black");
  useEffect(() => {
    const od = async () => {
      await axios
        .post("http://localhost:800/off/od", {
          username: sessionStorage.getItem("un"),
        })
        .then((res) => {
          setname(res.data.name);
          setdep(res.data.dep);
          setcol(res.data.col);
          if (res.data.status) {
            setsi("orange");
          } else {
            setso("orange");
          }
        });
    };
    od();
  });
  const i = async () => {
    await axios
      .post("http://localhost:800/off/in", {
        username: sessionStorage.getItem("un"),
      })
      .then((res) => {
        if (res.data.status === "sc") {
          window.location.reload(true);
        }
      });
  };
  const o = async () => {
    await axios
      .post("http://localhost:800/off/out", {
        username: sessionStorage.getItem("un"),
      })
      .then((res) => {
        if (res.data.status === "sc") {
          window.location.reload(true);
        }
      });
  };
  return (
    <div className="lanm">
      <div className="lanb">
        <div className="dcil">
          NAME : <span>{name}</span>
        </div>
        <div className="dcil">
          DEPARTMENT : <span>{dep}</span>
        </div>
        <div className="dcil">
          COLLEGE : <span>{col}</span>
        </div>
        <div className="dcil">
          STATUS :{" "}
          <button onClick={i} style={{ backgroundColor: `${si}` }}>
            IN
          </button>
          <button id="out" onClick={o} style={{ backgroundColor: `${so}` }}>
            OUT
          </button>
        </div>
      </div>
    </div>
  );
}
export default Lan;
