/* eslint-disable import/no-anonymous-default-export */
import React, { useEffect, useRef, useState, } from "react";
import './main.css'
export default ({data})=>{
  useEffect(()=>{},[])
  const [lev,setlev] = useState(false);
  const frm = useRef(null)
  const to = useRef(null)

  const tog = ()=>{
    setlev(p=>(!p))
  }
    return(
       <div className="main">
         <div>
            <p>person {data.present?'available':"not available"}</p>
            <label for='sel'>break for </label> 
            <select id="sel" value='select'>
                <option value='select'>select</option>
                <option value='0.25'>15 minutes</option>
                <option value='0.5'>30 minutes</option>
                <option value='0.75'>45 minutes</option>
                <option value='1'>1 hour</option>
                <option value='2'>2 hour</option>
                <option value='3'>3 hour</option>
            </select>
            <p>work time : {data.worktime.replace('-',' to ')}</p>
            <p>Holiday : {data.holiday}</p>
         </div>
         {data.present?
            <button type="button">Out</button>:
            <button type="button">In</button>}
          <div>
            <button onClick={tog} type="button">leave apply</button>
          </div>
          {lev&&<div className="leave">
            <div className="lev">      
              <div className="cls">
                <span onClick={tog} > X</span> <br />
              </div>
              <div className="fom">
                <label htmlFor="frm" >from :</label><input ref={frm} type="date" id="frm" /><br />
                <label htmlFor="ro" >&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;to : </label><input ref={to} type="date" id="ro" /><br />
                <button onClick={()=>{
                  console.log(frm.current.value,to.current.value)
                }}>Apply</button>
                <button onClick={()=>{
                  frm.current.value=''
                  to.current.value=''
                }}>reset</button>
            </div>
          </div>

          </div>}
      </div>
    )
}
